import javax.swing.*;import javax.swing.event.*;import javax.swing.tree.*;public class ViewJTreeextends JTree{	private static final long serialVersionUID = 1L;	protected ViewJTabbedPane m_ViewJTabbedPane;	public ViewJTree( DefaultMutableTreeNode root, ViewJTabbedPane vjtp )	{		super(root);		this.m_ViewJTabbedPane = vjtp;
		this.getSelectionModel().setSelectionMode(TreeSelectionModel.SINGLE_TREE_SELECTION);		this.addTreeSelectionListener( new ViewJTreeSelectionListener() );	}	private class ViewJTreeSelectionListener	implements TreeSelectionListener	{		public void valueChanged(TreeSelectionEvent evt)		{			try			{				DefaultMutableTreeNode node = (DefaultMutableTreeNode)getLastSelectedPathComponent();				if( node != null )				{					if( node.toString().equalsIgnoreCase("New Globe/VO/3D") )					{						ViewJTree.this.m_ViewJTabbedPane.createGlobeTab();					}					else if( node.toString().equalsIgnoreCase("New Map/2D") )					{						ViewJTree.this.m_ViewJTabbedPane.createMapTab();					}				}			}			catch( Throwable t )			{				t.printStackTrace();			}		}	}}